:root {
    --color_bg1: #1a2632;
    --color_bg2: #24292e;
    --color_bg3: #2b2b2b;
    --color_bg4: #24292e;
    --color_bg5: #3b3b3b;
    --color_bg6: #424242;
    --color_bg7: #4a4a4a;
    --color_bg8: #525252;

    --color_bg1_o09: rgba(29, 29, 29, 0.9);

    --color_accent1: #324038;
    --color_accent2: #00ab6b;
    --color_accent3: #02b875;
    --color_accent4: #02ed97;

    --color_fg1: #999691;
    --color_fg2: #a6a39d;
    --color_fg3: #b3b0aa;
    --color_fg4: #bfbcb6;
    --color_fg5: #ccc9c2;
    --color_fg6: #d9d5ce;
    --color_fg7: #e5e2da;
}

/* BACKGROUNDS */

.site-main, .screenContent, .canvas-renderer, .postActions, .readNextPromo {
    background: transparent !important;
}

html, body, .js-postList, .header, .footer, .network__profile:before, .zoomable:before {
