@namespace url(http://www.w3.org/1999/xhtml);
/*
VERSION HISTORY: 
  1.0: initial release
  1.1: made better nested list styling, fixed text while typing a new tag, fixed list of suggested tags while typing new tag, fixed border-overlap on tag bar, improved tags and border appearance, fixed background color on edit button in markdown mode, greatly improved the top buttos hover effects

# DEFAULTS: background=#23262b/#1f2326, button=#408ecb/rgb(64,142,203), text=#e5dada/rgb(229,218,218), link=#6dd1e2/rgb(109,209,226)
*/

.app {background-color: #1f2326; border-bottom: none;}
.note #static_content {padding: 1em 6em;} /*center note and give equal padding*/

.sideview, .tagbar, .toolbar, .searchfield, .sidebar {border-color: rgba(100%,100%,100%,0.1);}
.toolbar {border-bottom: none;}

/* search field, tag list, tag bar*/
.searchfield {box-shadow:none;}
.popover .menu, .tagbar .as-list {background: rgba(255,255,255,0.95);}
/* .popover .menu li a:hover, .popover .menu li a:focus {background: rgba(238,243,248,0.65);} may be able to combine this with the background color of the tagbar suggestion list if I alter the icon for the trashcan (causes issue with icon being same color as background) */
.notes li .note-preview-title, .searchfield, .tagbar input {color: white;}
.empty .button {background: none;}
.deleteTag {opacity: 1; filter: grayscale(1) brightness(4);} /* opacity is not working */
.deleteTag:hover {filter: none;}
.tagbar {padding-left: 0; left: 360px;} /* automattic didn't have the width of the tagbar correct, so they just messed with the left padding to fix it, which caused border-overlap, which caused my borders with transparency to look weird at one spot */
ul.as-selections li.as-selection-item:hover {color: #1f2326; background-color: rgb(64,142,203);}
.tagbar .as-list {width: 50% !important}
.tagbar .as-result-item {border: none; color: #1f2326;}
.popover .menu li a:hover, .popover .menu li a:focus, .tagbar .as-result-item.active {background: rgba(64,142,203,0.95);}
.tagbar .as-result-item.active:first-child {border-top-left-radius: inherit; border-top-right-radius: inherit;}
.tagbar .as-result-item.active:last-child {border-bottom-left-radius: inherit; border-bottom-right-radius: inherit;}

/* notes list */
.notes li {text-shadow: none;}
.notes li:hover {background-color: rgba(64,142,203,0.2);}
.notes li.selected, code {background:rgba(64,142,203,0.5);} /*selected note in list*/

/* text properties */
.note #static_content h2,
.note #static_content h1,
.note #static_content h3,
.note #static_content h4,
.note #static_content h5,
.note #static_content h6,
.note #static_content li,
.note #static_content p,
.note #static_content strong,
.note #txtarea
    {color: rgb(229,218,218); text-shadow: none;}

.note #static_content a {color: rgb(109,209,226)} /* links should be a little lighter to give less strain */
code {color: white;}
::-moz-selection {background: rgb(229,218,218); color: #1f2326; text-shadow: none;} /* change highlight properties */

/* change unordered list styles */
.note #static_content ul {list-style-type: disc;}
/* .note #static_content ul li:last-child {list-style-type: disclosure-closed;} */
.note #static_content ul li ul, .note #static_content ul li ul li ul li ul {list-style-type: circle;}
.note #static_content ul li ul li ul {list-style-type: square;}
.note #static_content ul li ul li ul li ul li ul {list-style-type: initial;}

/* change color of buttons and things */
.tagmenu.button span,
.header #user_account_menu,
.left-side-menu .sn-rocker-option
{
    color: rgb(64,142,203);
}

.left-side-menu .sn-rocker-option {
    text-shadow: none;
    background: #1f2326;
}

/*make a border around edit/preview button with markdown notes*/
#view_mode_edit.button.sn-rocker-option,
#view_mode_markdown.button.sn-rocker-option {
    border: 0.2em solid #408ecb;
}
.left-side-menu .sn-rocker-option.active,
.left-side-menu .sn-rocker-option.active:hover {
    background-color: rgb(64,142,203);
}
.left-side-menu .sn-rocker-option:hover{background-color: #333;}
.left-side-menu .sn-rocker-option{border: none;}

/* get rid of footer */
.wrapper {bottom: 0px;}
.footer {display: none;}

.notes li.selected {
    background: #1c1f22;
    border-right: 1px solid #448ac9;
}

