:root {
    
    --bg1: #111;
    --bg2: #151515;
    --bg3: #1a1a1a;
    --bg4: #222;
    
    --color1: #eee;
    --color2: #ccc;
    --color3: #aaa;
    
    --blue: #3498db;
    
  }
  
  

.style_card  {
    box-shadow: none !important;
  }
  
  
/*  BG1  */
  body, #nav-view, #navigation {
    background: var(--bg1) !important;
  }
  
/*  BG2  */
  .style_card, #footer, #style_installed_button, .edit-setting, .advancedsettings_shown {
    background: var(--bg2) !important;
  }
  
/*  BG3  */
  .navigation_link:hover, #header, #Head {
    background: var(--bg3) !important;
  }
  
/*  BG4  */
  
  
  
/*  COLOR1 (#eee)  */
  h4 a, h4, .navigation_title, #copyrights, h1, h4 a:visited, #information_value, #share_div, .title_setting {
    color: var(--color1) !important;
  }
  
/*  COLOR2 (#ccc)  */
  #headline, #information_key, .text_style_page, #applies_link {
    color: var(--color2) !important;
  }
  
/*  COLOR3 (#aaa)  */
  #information_key_left, #infomation_value_left {
    color: var(--color3) !important;
  }
  
/*  BLUE  */
  h4 a:hover, a {
    color: var(--blue) !important;
  }
  
  
  
  
  
/*  BUTTONS  */
  .see_more, .div-arrow, #css_button, #send_feedback_button, .white_button, .css_button, .advanced_button {
    background: var(--bg3) !important; color: var(--color1) !important; border: transparent !important;
  }
  .see_more:hover, .div-arrow:hover, #css_button:hover, #send_feedback_button:hover, .white_button:hover, .css_button:hover, .advanced_button:hover {
    background: var(--bg4) !important; color: var(--color1) !important; border: transparent !important;
  }
  
  
  
  
/*  BORDER TRANSPARENT  */
  .screenshot_wrapper {
    border-color: transparent !important;
  }
  
  
  
  
  
/*  SEPERATE RULES  */
  body {
    color: var(--color2) !important;
  }
  .linksBox .navigation_link:last-child, #single_discussion {
    box-shadow: 0 1px 0 0 #252525, 0 0 0 0 #252525;
  }
  #archive_style_button {
    box-shadow: none !important;
  }
  #archive_style_button:hover {
    background: #c14430; color: #fff;
  }
  #install_style_button {
    box-shadow: none;
  }
  #install_style_icon {
    margin-top: 5px;
  }
  #notes_textarea {
    background: var(--bg1) !important; color: var(--color2) !important;
    height: 400px;
    max-height: 400px;
  }
  #create_new_style:hover {
    background: var(--blue) !important; border-color: var(--blue) !important;
  }
  #create_new_style a, h4 a {
    transition: 0.2s ease; -webkit-transition: 0.2s ease;
  }
  #create_new_style:hover a {
    color: var(--color1) !important;
  }
  .form-controls input[type=text], .form-controls textarea, .form-controls input[type=url], #edit-style-settings input:not([type]), #edit-style-settings input[type="text"], .form-controls input[type=button] {
    background: var(--bg4) !important; border: 1px solid #444 !important; color: var(--color1) !important; box-sizing: border-box;
  }
  #style-settings select {
    background: #222;
    border: none;
    color: #fff;
    padding: 5px
  }
  #style-settings select option {
    padding: 2px 0;
  }
  .option-user-url {
    background: #222;
    border: none;
    color: #fff;
  }
  
  
/*  UPDATES  */
